import React, { Component } from "react";

class VideoViewNavbar extends Component {
  state = {
    views: "10000",
    title: "may may"
  };

  render() {
    return (
      <div>
        <span>{this.state.title}</span>
        <span>{this.getViews()}</span>
      </div>
    );
  }

  getViews() {
    const { views } = this.state;
    var si = [
      { value: 1, symbol: "" },
      { value: 1e3, symbol: "k" },
      { value: 1e6, symbol: "M" },
      { value: 1e9, symbol: "G" },
      { value: 1e12, symbol: "T" },
      { value: 1e15, symbol: "P" },
      { value: 1e18, symbol: "E" }
    ];
    var rx = /\.0+$|(\.[0-9]*[1-9])0+$/;
    var i;
    for (i = si.length - 1; i > 0; i--) {
      if (views >= si[i].value) {
        break;
      }
    }
    return (
      (views / si[i].value).toFixed(digits).replace(rx, "$1") + si[i].symbol
    );
  }
}

export default VideoViewNavbar;
